func (fdgb *FrontDeskGroupBy) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range fdgb.fields {
		if !frontdesk.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for group-by", f)}
		}
	}
	selector := fdgb.sqlQuery()
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := fdgb.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (fdgb *FrontDeskGroupBy) sqlQuery() *sql.Selector {
	selector := fdgb.sql.Select()
	aggregation := make([]string, 0, len(fdgb.fns))
	for _, fn := range fdgb.fns {
		aggregation = append(aggregation, fn(selector))
	}
	// If no columns were selected in a custom aggregation function, the default
	// selection is the fields used for "group-by", and the aggregation functions.
	if len(selector.SelectedColumns()) == 0 {
		columns := make([]string, 0, len(fdgb.fields)+len(fdgb.fns))
		for _, f := range fdgb.fields {
			columns = append(columns, selector.C(f))
		}
		columns = append(columns, aggregation...)
		selector.Select(columns...)
	}
	return selector.GroupBy(selector.Columns(fdgb.fields...)...)
}

// FrontDeskSelect is the builder for selecting fields of FrontDesk entities.
type FrontDeskSelect struct {
	*FrontDeskQuery
	// intermediate query (i.e. traversal path).
	sql *sql.Selector
}

// Scan applies the selector query and scans the result into the given value.
func (fds *FrontDeskSelect) Scan(ctx context.Context, v interface{}) error {
	if err := fds.prepareQuery(ctx); err != nil {
		return err
	}
	fds.sql = fds.FrontDeskQuery.sqlQuery(ctx)
	return fds.sqlScan(ctx, v)
}

